const express = require('express');
const router = express.Router();
const Product = require('../models/Product');

// GET tất cả sản phẩm
router.get('/', async (req, res) => {
  const products = await Product.find();
  res.json(products);
});

// POST thêm sản phẩm
router.post('/', async (req, res) => {
  const { name, price, imageUrl, description } = req.body;
  const newProduct = new Product({ name, price, imageUrl, description });
  await newProduct.save();
  res.status(201).json(newProduct);
});

// PUT cập nhật sản phẩm
router.put('/:id', async (req, res) => {
  const { id } = req.params;
  const { name, price, imageUrl, description } = req.body;
  const updated = await Product.findByIdAndUpdate(
    id,
    { name, price, imageUrl, description },
    { new: true }
  );
  if (!updated) return res.status(404).json({ error: 'Không tìm thấy sản phẩm' });
  res.json(updated);
});

// DELETE xóa sản phẩm
router.delete('/:id', async (req, res) => {
  const { id } = req.params;
  const deleted = await Product.findByIdAndDelete(id);
  if (!deleted) return res.status(404).json({ error: 'Không tìm thấy sản phẩm' });
  res.json({ message: 'Đã xóa sản phẩm' });
});

module.exports = router;
