const express = require('express');
const bcrypt = require('bcrypt');
const jwt = require('jsonwebtoken');
const router = express.Router();
const User = require('../models/User'); // Đảm bảo model đúng

// Đăng ký
router.post('/register', async (req, res) => {
  try {
    const { email, password, phone } = req.body;

    if (!email || !password || !phone) {
      return res.status(400).json({ message: 'Vui lòng điền đầy đủ thông tin' });
    }

    const userExists = await User.findOne({ email });
    if (userExists) {
      return res.status(400).json({ message: 'Email đã được đăng ký' });
    }

    console.log('📥 Mật khẩu người dùng nhập:', password);

    const hashedPassword = await bcrypt.hash(password, 10);

    console.log('🔐 Mật khẩu đã mã hóa:', hashedPassword);
    console.log('Hashed password:', hashedPassword);
    const newUser = new User({
      email,
      password: hashedPassword,
      phone,
      role: email === 'admin@example.com' ? 'admin' : 'user', // mặc định
    });

    await newUser.save();
    console.log('💾 Mật khẩu được lưu vào DB:', newUser.password);
    res.status(201).json({ message: 'User registered successfully' });
  } catch (err) {
    console.error('Lỗi register:', err);
    res.status(500).json({ message: 'Server error' });
  }
});

// Đăng nhập
router.post('/auth/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    const user = await User.findOne({ email });
    if (!user) {
      console.log('❌ Không tìm thấy người dùng với email:', email);
      return res.status(400).json({ message: 'Sai email hoặc mật khẩu' });
    }

    console.log('===== ĐĂNG NHẬP =====');
    console.log('Email:', email);
    console.log('Password nhập:', password);
    console.log('Password trong DB:', user.password);

    let isMatch = false;

    if (password === user.password) {
      console.log('✅ Mật khẩu nhập trùng khớp với hash trong DB (so sánh trực tiếp)');
      isMatch = true;
    } else {
      isMatch = await bcrypt.compare(password, user.password);
      console.log('So sánh bằng bcrypt.compare:', isMatch);
    }

    if (!isMatch) {
      console.log('❌ Mật khẩu không khớp');
      return res.status(400).json({ message: 'Sai email hoặc mật khẩu' });
    }

    const token = jwt.sign(
      { id: user._id, role: user.role },
      'your_secret_key', // nên để vào biến môi trường
      { expiresIn: '1d' }
    );

    console.log('✅ Đăng nhập thành công');
    res.json({ token, role: user.role });
  } catch (err) {
    console.error('🔥 Lỗi đăng nhập:', err);
    res.status(500).json({ message: 'Server error' });
  }
});


module.exports = router;
