const express = require('express');
const bcrypt = require('bcrypt');
const jwt = require('jsonwebtoken');
const router = express.Router();
const User = require('../models/User'); // Äáº£m báº£o model Ä‘Ãºng

// ÄÄƒng kÃ½
router.post('/register', async (req, res) => {
  try {
    const { email, password, phone } = req.body;

    if (!email || !password || !phone) {
      return res.status(400).json({ message: 'Vui lÃ²ng Ä‘iá»n Ä‘áº§y Ä‘á»§ thÃ´ng tin' });
    }

    const userExists = await User.findOne({ email });
    if (userExists) {
      return res.status(400).json({ message: 'Email Ä‘Ã£ Ä‘Æ°á»£c Ä‘Äƒng kÃ½' });
    }

    console.log('ğŸ“¥ Máº­t kháº©u ngÆ°á»i dÃ¹ng nháº­p:', password);

    const hashedPassword = await bcrypt.hash(password, 10);

    console.log('ğŸ” Máº­t kháº©u Ä‘Ã£ mÃ£ hÃ³a:', hashedPassword);
    console.log('Hashed password:', hashedPassword);
    const newUser = new User({
      email,
      password: hashedPassword,
      phone,
      role: email === 'admin@example.com' ? 'admin' : 'user', // máº·c Ä‘á»‹nh
    });

    await newUser.save();
    console.log('ğŸ’¾ Máº­t kháº©u Ä‘Æ°á»£c lÆ°u vÃ o DB:', newUser.password);
    res.status(201).json({ message: 'User registered successfully' });
  } catch (err) {
    console.error('Lá»—i register:', err);
    res.status(500).json({ message: 'Server error' });
  }
});

// ÄÄƒng nháº­p
router.post('/auth/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    const user = await User.findOne({ email });
    if (!user) {
      console.log('âŒ KhÃ´ng tÃ¬m tháº¥y ngÆ°á»i dÃ¹ng vá»›i email:', email);
      return res.status(400).json({ message: 'Sai email hoáº·c máº­t kháº©u' });
    }

    console.log('===== ÄÄ‚NG NHáº¬P =====');
    console.log('Email:', email);
    console.log('Password nháº­p:', password);
    console.log('Password trong DB:', user.password);

    let isMatch = false;

    if (password === user.password) {
      console.log('âœ… Máº­t kháº©u nháº­p trÃ¹ng khá»›p vá»›i hash trong DB (so sÃ¡nh trá»±c tiáº¿p)');
      isMatch = true;
    } else {
      isMatch = await bcrypt.compare(password, user.password);
      console.log('So sÃ¡nh báº±ng bcrypt.compare:', isMatch);
    }

    if (!isMatch) {
      console.log('âŒ Máº­t kháº©u khÃ´ng khá»›p');
      return res.status(400).json({ message: 'Sai email hoáº·c máº­t kháº©u' });
    }

    const token = jwt.sign(
      { id: user._id, role: user.role },
      'your_secret_key', // nÃªn Ä‘á»ƒ vÃ o biáº¿n mÃ´i trÆ°á»ng
      { expiresIn: '1d' }
    );

    console.log('âœ… ÄÄƒng nháº­p thÃ nh cÃ´ng');
    res.json({ token, role: user.role });
  } catch (err) {
    console.error('ğŸ”¥ Lá»—i Ä‘Äƒng nháº­p:', err);
    res.status(500).json({ message: 'Server error' });
  }
});


module.exports = router;
